import myWav_moduleimport osfrom cryptography.fernet import Fernetdef main():    #input data    file = input("Enter a wav file to be decrypted: ")    key = input("Please enter the key ")    decrypted_file_path = input("Enter the name the desired name of the file to be decrypted ")    if(decrypted_file_path == ""):        decrypted_file_path = file[:-4]+ "-decrypted.wav"         sample_rate, bits_per_sample, num_chans = myWav_module.split_wav(file)    encrypted_file_path = "content.bin"        cipher_suite = Fernet(key)        with open(encrypted_file_path, "rb") as encrypted_file, open("content2.bin", "wb") as decrypted_file:        while encrypted_chunk := encrypted_file.read(int(bits_per_sample/8)):            decrypted_chunk = cipher_suite.decrypt(encrypted_chunk)              decrypted_file.write(decrypted_chunk)                myWav_module.combine_files(decrypted_file_path , "header.bin", "content2.bin")                    if __name__ == "__main__":    main()